export default function EasyMode(){

        //pokemon list (number [0, 1, ...])
        let pokemonArray = []
        for(let i = 0; i < pokemonData.length; i++){
            pokemonArray.push(i)
        }
        console.log(pokemonArray)
        
        //options
        let options = []
        let totaloptionsToGenerate = 4
        let correctOption = Math.floor(Math.random() * options.length)
        
        //set random pokemon
        while(options.length<totaloptionsToGenerate){
            let randomOption = Math.floor(Math.random() * pokemonArray.length) 
            options.push(pokemonArray[randomOption]) 
            pokemonArray.splice(randomOption,1) 
            
        }

        // if(pokemonData[options])

        let round = []
        let currentRound = round.length + 1
        
        //setCorrectChoice
        
        
    return(
        <>
            <h1>Round {currentRound}</h1>
            <div className="page-bottom">
                <button className="choice-btn">{pokemonData[options[0]].name}</button>
                <button className="choice-btn">{pokemonData[options[1]].name}</button>
                <button className="choice-btn">{pokemonData[options[2]].name}</button>
                <button className="choice-btn">{pokemonData[options[3]].name}</button>
        
            </div>

        </>
    )
}
