import React, {useState, useEffect} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"
import axios from "axios"

export default function EasyMode(){
    //game set
    let [game, setGame] = useState({round: 1, options: {}})

    //pokedex
    const [allPokemon, setAllPokemon] = useState()

    useEffect(()=> {
        const controller = new AbortController()
        async function getAllPokemon(dexLimit){
            try{
                const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/?limit=${dexLimit}`, {signal: controller.signal})
                const pokemonData = res.data.results
                setAllPokemon(
                    pokemonData.map((pokemon, index) => ({id: index + 1, name: pokemon.name}))
                )
            } catch(error){
                console.log(error)
            }
        }
        getAllPokemon(905)
        return ()=> controller.abort()
    },[])


    function generateOptions(totalOptions){
        let pokemonArray = []
        while(game.options.length<totalOptions){
            let randomOption = Math.floor(Math.random() * allPokemon.length) 
            pokemonArray.push(allPokemon[randomOption]) 
            
        }    
        let answerNumber = options[Math.floor(Math.random()* options.length)]
        let correctAnswer = allPokemon[answerNumber]

        setGame(prevGame => ({...prevGame, options: pokemonArray}))
    }



    //cry audio
    let audio = new Audio(require(`../pokecries/${correctAnswer}.ogg`))

    console.log("correct answer: " + pokemonData[correctAnswer - 1].name)
    
    function checkAnswer(num){
        if(pokemonData[options[num]].id === correctAnswer){
            setGame(prevGame => ({...prevGame, round: prevGame.round + 1}))
            console.log("Correct") 
            
        }else {
            setGame(prevGame => ({...prevGame, round: 1}))
            console.log("Wrong")
        }

    }



    return(
        <>
            <h1>Round {game.round}</h1>
            <div className="audioPlayer">
                <button id="audio-btn" onClick={()=> audio.play()}>Sound</button>
            </div>

            <div className="page-bottom">
                <button className="choice-btn" onClick={()=>checkAnswer(0)}>{pokemonData[options[0]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(1)}>{pokemonData[options[1]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(2)}>{pokemonData[options[2]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(3)}>{pokemonData[options[3]].name}</button>
        
            </div>

            <Link to="/ChooseGamemode"><button id="back-btn">Back</button></Link>

        </>
    )
}
