import React, {useState} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"

export default function EasyMode(){

    const pokemonArray = []
    for(let i = 0; i < pokemonData.length; i++){
        pokemonArray.push(i)
    }

    function randomPokemon(){Math.floor(Math.random() * pokemonArray.length + 1)}
    const randomPokemon2 = Math.floor(Math.random() * pokemonArray.length + 1) 
    const randomPokemon3 = Math.floor(Math.random() * pokemonArray.length + 1) 
    const randomPokemon4 = Math.floor(Math.random() * pokemonArray.length + 1) 
    
    

    return(
        <div className="page-bottom">
            <button className="choice-btn">{randomPokemon()}</button>
            <button className="choice-btn">{randomPokemon2}</button>
            <button className="choice-btn">{randomPokemon3}</button>
            <button className="choice-btn">{randomPokemon4}</button>
        
        </div>
    )
}
