import React, {useState, useEffect} from "react"
import { Link } from "react-router-dom";


export default function Leaderboard(){
    const [leaderboardData, setLeaderboardData] = useState({});
   
    useEffect(() => {
        const storedLeaderboard = localStorage.getItem('leaderboard');
        
        // We need to check if storedLeaderboard is not null before parsing it
        const data = storedLeaderboard ? JSON.parse(storedLeaderboard) : [];
        // If 'data' is not an array, we convert it to an array using Object.values
        if (!Array.isArray(data)) {
            const leaderboardArray = Object.values(data);
            setLeaderboardData(leaderboardArray);
        } else {
            setLeaderboardData(data);
        }
        console.log(storedLeaderboard)
    }, []);
    

    return(
        <>
            <div className="page-top">
                <h1 className="title" id="title-main">Leaderboard</h1>

            </div>

            <div className="leaderboard">
                <table>
                    <thead>
                        <tr>
                            <th>Rank</th>
                            <th>Username</th>
                            <th>Guesses</th>
                        </tr>
                    </thead>
                    <tbody>
                        {leaderboardData.map((key, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{key.Username}</td>
                                <td className="color-blue">{key.Score}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="page-bottom">
                <div className="row" >
                    <Link to="/"><button className="button-default">Return</button></Link>
                </div>
            </div>
        </>
    )
}
