import React, {useState, useEffect} from "react"
import { Link, useNavigate } from "react-router-dom";
import Options from "./Options.js"
import leaderboard from "../../leaderboard.json";




export default function GameOver({game, gameMode}){

    const navigate = useNavigate()

    function recordScore(json, username, score) {
        json[username] = {
            "Username": username,
            "Score": score
        };
    }

    function handleSubmit(event) {
        event.preventDefault();
        recordScore(leaderboard, username, game.score); 
    }

    function handleTryAgain() {
        switch (gameMode) {
            case "easy":
                navigate("/EasyMode", { state: { numOptions: 4, numLives: 3 } });
                break;
            case "hard":
                navigate("/HardMode", { state: { numOptions: 16, numLives: 1 } }); 
                break;
            case "insane":
                navigate("/InsaneMode", { state: { numOptions: 32, numLives: 0 } }); 
                break;
            default:
        }
    }

    return(
        <>
            <div className="page-top">
                <h1 className="gameOver">GAME OVER...</h1> 
                <h1>You had a total of {game.guesses}</h1>

            </div>

            
            <div className="page-bottom">

                <div className="column">
                    <h1>Insert your name below</h1>
                    <input className="insert-name"></input>
                </div>

                <div className="row" >
                    <Link to="/ChooseDifficulty"><button className="button-default">Back</button></Link>
                    <button className="button-default" onClick={()=> handleTryAgain()}>Try Again</button>
                    <Link to="/"><button className="button-default">Return</button></Link>
                </div>

            </div>
        </>
    )
}
