import React, {useState, useEffect} from "react"
import { Link } from "react-router-dom";


export default function Leaderboard(){
    const [leaderboardData, setLeaderboardData] = useState([]);
   
    useEffect(() => {
        const storedLeaderboard = localStorage.getItem('leaderboard_insane');
        const data = storedLeaderboard ? JSON.parse(storedLeaderboard) : []

        const sortedData = Array.isArray(data)
            ? data.sort((a, b) => b.Score - a.Score).slice(0, 100)
            : [];

            setLeaderboardData(sortedData);
    }, []);

    return(
        <>
            <div className="page-top">
                <h1 className="title" id="title-main">Leaderboard Classic: Insane Mode</h1>

            </div>

            <div className="leaderboard">
                <table>
                    <thead>
                        <tr>
                            <th>Rank</th>
                            <th>Username</th>
                            <th>Guesses</th>
                        </tr>
                    </thead>
                    <tbody>
                        {leaderboardData.map((key, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
             s                   <td>{key.Username}</td>
                                <td className="color-blue">{key.Score}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="page-bottom">
                <div className="row" >
                    <Link to="/"><button className="button-default">Return</button></Link>
                </div>
            </div>
        </>
    )
}
