import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";
import PokemonSquare from "./PokemonSquare";


export default function Pokedex(){

    const [gen1, setGen1] = useState([])
    const [gen2, setGen2] = useState([])
    const [gen3, setGen3] = useState([])
    const [gen4, setGen4] = useState([])
    const [gen5, setGen5] = useState([])
    const [gen6, setGen6] = useState([])
    const [gen7, setGen7] = useState([])
    const [gen8, setGen8] = useState([])
    const [selectedGen, setSelectedGen] = useState([])

    const [allPokemon, setAllPokemon] = useState([])

    useEffect(()=> {
        async function getAllPokemon(dexLimit){
            const res = await fetch(`https://pokeapi.co/api/v2/pokemon/?limit=${dexLimit}`)
            const pokemonData = await res.json()
            setAllPokemon(
                pokemonData.results.map((pokemon, index) => ({id: index + 1, name: pokemon.name}))
            )  
        } getAllPokemon(905)
    },[])

    function generateGenerationsPokemonLists(){
        let pokemonArray = allPokemon.map(mon => mon) 

        //gen1
        let gen1 = []
        for(let i = 1; i<151; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen1.push({pokemon: pokemon, audio: cry});
        }
        setGen1(gen1)
        //gen2
        let gen2 = []
        for(let i = 152; i<251; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen2.push({pokemon: pokemon, audio: cry});
        }
        setGen2(gen2)
        //gen3
        let gen3 = []
        for(let i = 252; i<386; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen3.push({pokemon: pokemon, audio: cry});
        }
        setGen3(gen3)
        //gen4
        let gen4 = []
        for(let i = 387; i<493; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen4.push({pokemon: pokemon, audio: cry});
        }
        setGen4(gen4)
        //gen5
        let gen5 = []
        for(let i = 494; i<649; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen5.push({pokemon: pokemon, audio: cry});
        }
        setGen5(gen5)
        //gen6
        let gen6 = []
        for(let i = 645; i<721; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen6.push({pokemon: pokemon, audio: cry});
        }
        setGen6(gen6)
        //gen7
        let gen7 = []
        for(let i = 722; i<802; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen7.push({pokemon: pokemon, audio: cry});
        }
        setGen7(gen7)
        //gen8
        let gen8 = []
        for(let i = 803; i<905; i++){
            let pokemon = pokemonArray[i - 1]
            let cry = `../pokecries/${pokemon.id}.ogg`;
            gen8.push({pokemon: pokemon, audio: cry});
        }
        setGen8(gen8)
    }

    function playSound(cryPath){
        let audio = new Audio(require(cryPath))
        audio.volume = 0.1
    }

    function renderPokedex(generation){
        return <div className="divOptions">{generation.map(option => <PokemonSquare playSound={playSound(option.cry)} />)}</div>
    }

    useEffect(()=> {
        generateGenerationsPokemonLists()
    }, [allPokemon])
   
    return(
        <>
            <div className="page-top">
                <div className="title">
                    <h1 id="title-main">Pokedex</h1>
                    <h4 id="subtitle"></h4>

                </div>
                <div className="row">
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen1)}}>Gen1</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen2)}}>Gen2</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen3)}}>Gen3</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen4)}}>Gen4</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen5)}}>Gen5</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen6)}}>Gen6</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen7)}}>Gen7</button>
                    <button className="button-thin" onClick={()=>{setSelectedGen(gen8)}}>Gen8</button>
                </div>
            </div>
            <div className="mid">
                {renderPokedex(selectedGen)}
            </div>
            <div className="page-bottom">
                <Link to="/"><button className="button-default">Return</button></Link>
            </div>
            
        </>
    )
}
