import React, {useState} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"

export default function EasyMode(){

    function randomPokemon(lastrandom){ 
        Math.floor(Math.random() * pokemonData.length + 1)

        if(randomPokemon === lastrandom){Math.floor(Math.random() * pokemonData.length + 1)}
    }

    const randomPokemon2 = Math.floor(Math.random() * pokemonData.length + 1) 
    const randomPokemon3 = Math.floor(Math.random() * pokemonData.length + 1) 
    const randomPokemon4 = Math.floor(Math.random() * pokemonData.length + 1) 
    
    

    return(
        <div className="page-bottom">
            <button className="choice-btn">{randomPokemon}</button>
            <button className="choice-btn">{randomPokemon2}</button>
            <button className="choice-btn">{randomPokemon3}</button>
            <button className="choice-btn">{randomPokemon4}</button>
        
        </div>
    )
}
