import React, {useState, useEffect} from "react"
import { Link, useNavigate } from "react-router-dom";
import Options from "./Options.js"
import leaderboard from "../../leaderboard.json";




export default function GameOver({game}){
    
    const navigate = useNavigate()

    function recordScore(json, username, score) {
        json[username] = {
            "Username": username,
            "Score": score
        };
    }

    return(
        <>
            <div className="page-top">
                <h1 className="gameOver">GAME OVER...</h1> 
                <h1>You had a total of {game.guesses}</h1>

            </div>

            
            <div className="page-bottom">

                <div className="column">
                    <h1>Insert your name below</h1>
                    <input className="insert-name"></input>
                </div>

                <div className="row" >
                    <Link to="/ChooseDifficulty"><button className="button-default">Back</button></Link>
                    <button className="button-default" onClick={()=> navigate("./{GameOver}.js")}>Try Again</button>
                    <Link to="/"><button className="button-default">Return</button></Link>
                </div>

            </div>
        </>
    )
}
