import React, {useState, useEffect} from "react"
import { Outlet, Link } from "react-router-dom";
import Options from "./Options.js"



export default function GuessTheCry({numRounds, numOptions, numLives}){
    //set game
    let [game, setGame] = useState({
        status: null, round: 1, options: {currentOptions: [], correctAnswer: {pokemon: null, audio: null}}
    })

    //generate pokedex
    const [allPokemon, setAllPokemon] = useState([])

    useEffect(()=> {
        // const controller = new AbortController()
        async function getAllPokemon(dexLimit){
                const res = await fetch(`https://pokeapi.co/api/v2/pokemon/?limit=${dexLimit}`)/*, {signal: controller.signal})*/
                const pokemonData = await res.json()
                setAllPokemon(
                    pokemonData.results.map((pokemon, index) => ({id: index + 1, name: pokemon.name}))
                )  
        } getAllPokemon(721)
        // return ()=> controller.abort()
    },[])

    function generateOptions(totalOptions){
        let pokemonArray = allPokemon.map(mon => mon) 
        let optionsArray = []

        function generate(){
            let randomOption = Math.floor(Math.random() * pokemonArray.length) 
            let foundOption = pokemonArray[randomOption]
            
            while(optionsArray.length<totalOptions){
                let randomOption = Math.floor(Math.random() * pokemonArray.length) 
                let foundOption = pokemonArray[randomOption]
                
                optionsArray.push(foundOption) 
                pokemonArray = pokemonArray.filter(mon => mon !== foundOption)
            }  
        }

        let answerNumber = Math.floor(Math.random()* totalOptions)
        let correctAnswer = optionsArray[answerNumber]
        console.log(correctAnswer)
        let audio = new Audio(require(`../../pokecries/${correctAnswer.id}.ogg`))
            audio.volume = 0.1

        setGame(prevGame => ({...prevGame, 
            options: {
                currentOptions: optionsArray,
                correctAnswer: {
                    pokemon: correctAnswer,
                    audio: audio
                }
        }}))
    }


    
    function checkAnswer(option){
        if(option === game.options.correctAnswer.pokemon){
            console.log("Correct") 
            setGame(prevGame => ({...prevGame, round: prevGame.round + 1}))
            generateOptions(numOptions)
            
        }else {
            console.log("Wrong")
            setGame(prevGame => ({...prevGame, round: 1}))
            generateOptions(numOptions)
            
        }
    }

    function renderOptions(totalOptions){
        return totalOptions.map(option => <Options /*key={option.id}*/ checkAnswer={checkAnswer} option={option}/>)
    }

    function startGame(){
        setGame(prevGame => ({...prevGame, status: "started"}))
        generateOptions(numOptions)
    }

    return(
        <>
            {game.status !== null ? <h1 id="title-round">Round {game.round}</h1> : null}
            <div className="audioPlayer">
            {game.status !== null ? <button id="audio-btn" onClick={()=> game.options.correctAnswer.audio.play()}>Sound</button> : null}
            </div>

            
            
            <div className="page-bottom">
                {game.status === null ? <button class="button-default" onClick={()=> startGame()}>START</button> : renderOptions(game.options.currentOptions)}

                <Link to="/ChooseGamemode"><button class="button-default">Back</button></Link>

            </div>

        </>
    )
}
