import React, {useState, useEffect} from "react"
import { Link } from "react-router-dom";
import Options from "./Options.js"
import axios from "axios"


export default function GuessTheCry({numRounds, numOptions, numLives}){
    //game set
    let [game, setGame] = useState({round: 1, options: {currentOptions: [], correctAnswer: {pokemon: null, audio: null}}})

    //pokedex
    const [allPokemon, setAllPokemon] = useState()

    useEffect(()=> {
        const controller = new AbortController()
        async function getAllPokemon(dexLimit){

                const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/?limit=${dexLimit}`, {signal: controller.signal})
                const pokemonData = res.results
                    setAllPokemon(
                        pokemonData.map((pokemon, index) => ({id: index + 1, name: pokemon.name}))
                    )  
                    console.log(allPokemon)
        } getAllPokemon(721)
        return ()=> controller.abort()
    },[])

    function generateOptions(totalOptions){
        let options = game.options.currentOptions
        let pokemonArray = []
        while(options.length<totalOptions){
            let randomOption = Math.floor(Math.random() * allPokemon.length) 
            pokemonArray.push(allPokemon[randomOption]) 
            
        }    
        let answerNumber = options[Math.floor(Math.random()* options.length)]
        let correctAnswer = allPokemon[answerNumber]
        
        let audio = new Audio(require(`../../pokecries/${correctAnswer}.ogg`))

        setGame(prevGame => ({...prevGame, 
            options: {
                currentOptions: pokemonArray,
                correctAnswer: {
                    pokemon: correctAnswer,
                    audio: audio
                }
        }}))
    }


    
    function checkAnswer(option){
        if(option === game.options.correctAnswer){
            setGame(prevGame => ({...prevGame, round: prevGame.round + 1}))
            generateOptions(numOptions)
            console.log("Correct") 
            
        }else {
            setGame(prevGame => ({...prevGame, round: 1}))
            console.log("Wrong")
        }

    }

    function renderOptions(totalOptions){
        generateOptions(numOptions)
        return totalOptions.map(option => <Options checkAnswer={checkAnswer} option={option}/>)
    }

    return(
        <>
            <h1>Round {game.round}</h1>
            <div className="audioPlayer">
                <button id="audio-btn" onClick={()=> game.options.correctAnswer.audio.play()}>Sound</button>
            </div>

 

            
            {
            allPokemon && <div className="page-bottom">
            {renderOptions(game.options.currentOptions)}

            </div>
            }

            

            <Link to="/ChooseGamemode"><button id="back-btn">Back</button></Link>

        </>
    )
}
