import React, {useState} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"

export default function EasyMode(){


    //pokemon list (number [0, 1, ...])
    let pokemonArray = []
    for(let i = 0; i < pokemonData.length; i++){
        pokemonArray.push(i)
    }
    
    //options
    let options = []
    let totaloptionsToGenerate = 4
    
    //set random pokemon
    while(options.length<totaloptionsToGenerate){
        let randomOption = Math.floor(Math.random() * pokemonArray.length) 
        options.push(pokemonArray[randomOption]) 
        pokemonArray.splice(randomOption,1) 

    }

    // round counter
    let round = []
    let currentRound = round.length + 1
    
    //setCorrectAnswer
    let correctAnswer = Math.floor(Math.random()* options.length + 1)
    console.log(correctAnswer)

    function checkAnswer(){
        if(pokemonData[options[1]] === correctAnswer){
            round.push(1)
        }
    }

    return(
        <>
            <h1>Round {currentRound}</h1>
            <div className="page-bottom">
                <button className="choice-btn" onClick={checkAnswer}>{pokemonData[options[0]].name}</button>
                <button className="choice-btn" onClick={checkAnswer}>{pokemonData[options[1]].name}</button>
                <button className="choice-btn" onClick={checkAnswer}>{pokemonData[options[2]].name}</button>
                <button className="choice-btn" onClick={checkAnswer}>{pokemonData[options[3]].name}</button>
        
            </div>

        </>
    )
}
