import React, {useState} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"

export default function EasyMode(){

        //pokemon list (number [0, 1, ...])
        let pokemonArray = []
        for(let i = 0; i < pokemonData.length; i++){
            pokemonArray.push(i)
        }
        console.log(pokemonArray)
        
        let options = []
        let totaloptionsToGenerate = 4
        
        
        while(options.length<totaloptionsToGenerate){
            let randomOption = Math.floor(Math.random() * pokemonArray.length) //num
            let randomArrayItem = pokemonArray[randomOption] //num2
            pokemonArray.splice(randomOption,1) //pokemonArray - randomOption(num)
            options.push(randomArrayItem) //options + num2
            console.log("pokemonArray:" , pokemonArray, "options: ", "randomOption: ", randomOption, options, "randomArrayItem:" , randomArrayItem)
        }

    
    // generatePokemon()

    return(
        
        <div className="page-bottom">
            <button className="choice-btn">{pokemonData[options[0]].name}</button>
            <button className="choice-btn">{options[1]}</button>
            <button className="choice-btn">{options[2]}</button>
            <button className="choice-btn">{options[3]}</button>
        
        </div>
    )
}
