import React, {useState} from "react"
import { Outlet, Link } from "react-router-dom";
import pokemonData from "../pokemonData.js"

export default function EasyMode(){

    let [setRound, round] = useState(1)

    //pokemon list ([0, 1, ...])
    let pokemonArray = []
    for(let i = 0; i < pokemonData.length; i++){
        pokemonArray.push(i)
    }

    //options
    let options = []
    let totaloptionsToGenerate = 4
    
    //set random pokemon
    while(options.length<totaloptionsToGenerate){
        let randomOption = Math.floor(Math.random() * pokemonArray.length) 
        options.push(pokemonArray[randomOption]) 
        pokemonArray.splice(randomOption,1) 

    }

    //setCorrectAnswer
    let answerNumber = options[Math.floor(Math.random()* options.length)]
    let correctAnswer = pokemonData[answerNumber].id

    console.log("correct answer: " + pokemonData[correctAnswer - 1].name)
    
    function checkAnswer(num){
        if(pokemonData[options[num]].id === correctAnswer){
            setRound(round + 1)
            console.log("Correct") 
            
        }else {console.log("Wrong")}

    }

    return(
        <>
            <h1>Round {round}</h1>
            <div className="page-bottom">
                <button className="choice-btn" onClick={()=>checkAnswer(0)}>{pokemonData[options[0]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(1)}>{pokemonData[options[1]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(2)}>{pokemonData[options[2]].name}</button>
                <button className="choice-btn" onClick={()=>checkAnswer(3)}>{pokemonData[options[3]].name}</button>
        
            </div>

        </>
    )
}
